mapper.select:
select * from seckill_activity


        and name like concat('%',#{name},'%')


        and status = #{status}


        and start_time >= #{startTime}


        and end_time <= #{endTime}


order by create_time desc

mapper.update:
update seckill_activity
name = #{name}, image = #{image}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}
