mapper.select:
select so.*, o.number, o.status, o.user_id, o.order_time, o.checkout_time, o.pay_method, 
       o.pay_status, o.amount, o.phone, o.address, o.user_name, o.consignee
from seckill_order so
left join orders o on so.order_id = o.id


        and o.number like concat('%',#{number},'%')


        and o.phone like concat('%',#{phone},'%')


        and o.status = #{status}


        and o.order_time >= #{beginTime}


        and o.order_time <= #{endTime}


order by o.order_time desc

select so.*, o.number, o.status, o.order_time, o.pay_status, o.amount
from seckill_order so
left join orders o on so.order_id = o.id
where so.user_id = #{userId}

    and o.status = #{status}

order by o.order_time desc
